import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom'
import jwt_decode from 'jwt-decode'

import GetComments from './GetComments.js'

class GetPosts extends Component{
	constructor(){
        super()
        this.state = {
            userId: 0,
            posts: []        
        }
        this.getPost = this.getPost.bind(this)
        this.deletePost = this.deletePost.bind(this)
        this.addComment = this.addComment.bind(this)

    }

    addComment(postId){

    }

    deletePost(postId){
        const post = {
            postId: postId,

        }
        fetch('http://localhost:3000/delete-post/?userId='+postId,
        {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(post)
          })
            .then((response) => { return response.json() })
            .then((res) => {
                console.log(res.result)

            })
            .catch((e) => { console.log(e)});

    }


    getPost(userId){
    	fetch('http://localhost:3000/get-posts/?userId='+userId)
            .then((response) => { return response.json() })
            .then((res) => {
                console.log(res.result)
                this.setState({posts: res.result})
            })
            .catch((e) => { console.log(e)});
    }

    componentDidMount(){
      if(localStorage.usertoken){
        var token = localStorage.usertoken
        var decoded = jwt_decode(token)
        this.setState({
            userId: decoded.userId
        })
        fetch('http://localhost:3000/get-posts/?userId='+this.props.id)
            .then((response) => { return response.json() })
            .then((res) => {
                console.log(res.result)
                this.setState({posts: res.result})
            })
            .catch((e) => { console.log(e)});
      }
    }

    render(){

    	const PostList = () => {
    		const options = this.state.posts.map((i) => (
        		<div>	
                    <ul>
                        <Link to = {'/profile/?userId='+i.userId}>
                            <li>{i.username}</li>    
                        </Link> 
                        {i.content}
                        <br/>
                        {this.props.id == this.state.userId ? 
                            <Button onClick = {() => this.deletePost(i.postId)}> Delete</Button> : ""}
                            <Button onClick = {() => this.addComment(i.postId)}> Add Comment</Button> 
                        <GetComments id = {i.userId}/>
                    </ul>
                </div>    
	        ))
	        return <ul>{options}</ul>
	     }
    	return(
    		<div>
    			<PostList/>
    		</div>
    	)
    }
}
export default GetPosts;
