import React, { Component } from 'react'
import { Button } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom'
import Login from './Login'
import Register from './Register'
import jwt_decode from 'jwt-decode'

class Navbar extends Component {
    constructor(){
        super()
        this.state = {
            userId: 0
        }
        this.goToProfile = this.goToProfile.bind(this)
    }

    goToProfile(e){
        e.preventDefault()
        this.props.history.push(`/profile/?userId=`+this.state.userId)
    }
    componentDidMount(){
        if(localStorage.usertoken){
            var token = localStorage.usertoken
            var decoded = jwt_decode(token)
            this.setState({
                userId: decoded.userId
            })
        }
    }
    logOut (e) {
        e.preventDefault()
        localStorage.removeItem('usertoken')
        this.props.history.push('/')
    }

    render () {
        {/*==================== Content of menu changes depending if user is logged in or not ====================*/}
        const loginRegLink = (
            <div className="header">
                <table>
                    <tr>
                        <td>
                            <Login history = {this.props.history}/>
                        </td>
                    </tr>
                 </table>   
             </div>           


        )

        const userLink = (
            <div className="header">
                <table>
                    <tr><p></p></tr>
                    <tr><p></p></tr>
                    <tr>
                    <td>
                        <Button onClick = {this.goToProfile.bind(this)}> Profile </Button>
                    </td>
                    <td>
                        <Link to="" onClick={this.logOut.bind(this)}>
                            <Button className="header-button">Logout</Button>
                        </Link>

                    </td>
                    </tr>

                 </table>   
             </div>           

        )
        {/*=======================================================================================================*/}
        return (
            <header className="App-header">
                <div className="clogo">
                    {localStorage.usertoken ? <Link to="/wall">Social Media </Link>:<Link to="/">Social Media </Link>}
                </div>
                {localStorage.usertoken ? userLink : loginRegLink}
            </header>
        )
    }
}

export default withRouter(Navbar)
