import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom'
import jwt_decode from 'jwt-decode'

class GetFriendList extends Component{
	constructor(){
        super()
        this.state = {
            userId: 0,
            friends: []        
        }
    }

    componentDidMount(){
      if(localStorage.usertoken){
        var token = localStorage.usertoken
        var decoded = jwt_decode(token)
        this.setState({
            userId: decoded.userId
        })
        fetch('http://localhost:3000/get-friends/?userId='+decoded.userId)
            .then((response) => { return response.json() })
            .then((res) => {
                console.log(res.result)
                this.setState({friends: res.result})
            })
            .catch((e) => { console.log(e)});
      }
    }

    render(){
    	const Suggestions = (props) => {
          const data = props  
          const options = this.state.friends.map((r) => (
            <Link to={"/shop/?shopName="+r.name+"&shopId="+r.shopId}> 
              <div className="search-item">
                <table class = "text-left">

                    <td> 
                      <li key={r.shopId}>
                        <div class="image">
                          {/*<Image class="d-block w-100" src={pic1} alt="Third slide" className="pic2" rounded/>*/}
                          <h5><span>
                            {r.name}

                          </span></h5>
                          <h6><span>Description: {r.description} </span></h6>

                        </div>
                    </li>
                    </td>

               </table>
              </div>
            </Link>
          ))
          return <ul>{options}</ul>
        }
    	return(
    		<div>
    			<Suggestions data = {this.state.friends}/>
    		</div>
    	)
    }
}
export default GetFriendList;
