import React, { Component } from 'react'
import { Button } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom'
import Login from './Login'
import Register from './Register'
import jwt_decode from 'jwt-decode'

class Navbar extends Component {
    constructor(){
        super()
        this.state = {
            userId: 0,
            users: []
        }
    }

    searchUser(e){
    const query = e.target.value;

    fetch('http://localhost:3000/search-user/?name='+query)
          .then((response) => { return response.json() })
          .then((result) => {
              console.log(result)
              this.setState({shops: result})
          })
          .catch((e) => { console.log(e)});

  }

    componentDidMount(){
        if(localStorage.usertoken){
            var token = localStorage.usertoken
            var decoded = jwt_decode(token)
            this.setState({
                userId: decoded.userId
            })
        }
    }
    logOut (e) {
        e.preventDefault()
        localStorage.removeItem('usertoken')
        this.props.history.push('/')
    }

    render () {
        {/*==================== Content of menu changes depending if user is logged in or not ====================*/}
        const loginRegLink = (
            <div className="header">
                <table>
                    <tr>
                        <td>
                            <Login history = {this.props.history}/>
                        </td>
                    </tr>
                 </table>   
             </div>           


        )

        const userLink = (
            <div className="header">
                <table>
                    <tr><p></p></tr>
                    <tr><p></p></tr>
                    <tr>
                    <td>
                        <Link to = {"/profile/?userId="+this.state.userId}>
                            <Button className="header-button"> Profile </Button>
                        </Link>
                    </td>
                    <td>
                        <Link to="" onClick={this.logOut.bind(this)}>
                            <Button className="header-button">Logout</Button>
                        </Link>

                    </td>
                    </tr>

                 </table>   
             </div>           

        )
        {/*=======================================================================================================*/}
        return (
            <header className="App-header">
                <div className="clogo">
                    {localStorage.usertoken ? <Link to="/wall">Social Media </Link>:<Link to="/">Social Media </Link>}
                </div>
                {localStorage.usertoken ? userLink : loginRegLink}
                <article className="csearch-bars">
                      <input type="text" 
                        className="search" 
                        name="Search"
                        placeholder="Search for restaurants..."
                        value={this.state.query}
                        onChange={this.searchUser}
                      />
                      <Suggestions results = {this.state.users} />
                    </article>
            </header>
        )
    }
}

export default withRouter(Navbar)
